import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Table,
  Spinner,
  Modal,
  Form,
} from "react-bootstrap";
import api from "../../api"; // ‚úÖ axios instance with baseURL & token interceptor
import { useAuth } from "../../auth/AuthContext";

export default function CivilianDashboard() {
  const { user } = useAuth();
  const [cases, setCases] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCaseModal, setShowCaseModal] = useState(false);
  const [newCase, setNewCase] = useState({ title: "", description: "" });

  // --- Fetch cases + payments on load ---
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [caseRes, paymentRes] = await Promise.all([
          api.get("/cases"),
          api.get("/payments"),
        ]);
        setCases(caseRes.data || []);
        setPayments(paymentRes.data || []);
      } catch (err) {
        console.error("‚ùå Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // --- File new case ---
  const handleFileCase = async () => {
    try {
      const res = await api.post("/cases", newCase);
      setCases([...cases, res.data]);
      setShowCaseModal(false);
      setNewCase({ title: "", description: "" });
    } catch (err) {
      console.error("‚ùå Failed to file case:", err);
    }
  };

  return (
    <Container className="mt-4">
      <h3>
        üëã Welcome, <b>{user?.email}</b>
      </h3>
      <p className="text-muted">
        You can file cases, track progress, upload evidence, and manage
        payments.
      </p>

      {loading ? (
        <div className="d-flex justify-content-center my-5">
          <Spinner animation="border" role="status" />
        </div>
      ) : (
        <>
          {/* Cases */}
          <Row className="mt-4">
            <Col md={12}>
              <Card className="shadow-sm">
                <Card.Body>
                  <Card.Title>üìë My Cases</Card.Title>
                  {cases.length > 0 ? (
                    <Table striped bordered hover>
                      <thead>
                        <tr>
                          <th>#</th>
                          <th>Case Title</th>
                          <th>Status</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {cases.map((c, i) => (
                          <tr key={c.id}>
                            <td>{i + 1}</td>
                            <td>{c.title}</td>
                            <td>{c.status}</td>
                            <td>
                              <Button
                                size="sm"
                                variant="info"
                                href={`/civilian/cases/${c.id}`}
                              >
                                View
                              </Button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  ) : (
                    <p>No cases filed yet.</p>
                  )}
                  <Button
                    variant="success"
                    onClick={() => setShowCaseModal(true)}
                  >
                    + File New Case
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* Evidence + Payments */}
          <Row className="mt-4">
            <Col md={6}>
              <Card className="shadow-sm">
                <Card.Body>
                  <Card.Title>üìÇ Evidence</Card.Title>
                  <Card.Text>
                    Upload documents, images, or videos as evidence.
                  </Card.Text>
                  <Button variant="primary" href="/civilian/evidence">
                    Upload Evidence
                  </Button>
                </Card.Body>
              </Card>
            </Col>

            <Col md={6}>
              <Card className="shadow-sm">
                <Card.Body>
                  <Card.Title>üí≥ Payments</Card.Title>
                  {payments.length > 0 ? (
                    <Table size="sm" bordered>
                      <thead>
                        <tr>
                          <th>#</th>
                          <th>Amount</th>
                          <th>Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {payments.map((p, i) => (
                          <tr key={p.id}>
                            <td>{i + 1}</td>
                            <td>${p.amount}</td>
                            <td>{p.status}</td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  ) : (
                    <p>No payments yet.</p>
                  )}
                  <Button variant="warning" href="/civilian/payments">
                    View All Payments
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </>
      )}

      {/* File New Case Modal */}
      <Modal show={showCaseModal} onHide={() => setShowCaseModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>üìù File New Case</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Case Title</Form.Label>
              <Form.Control
                type="text"
                value={newCase.title}
                onChange={(e) =>
                  setNewCase({ ...newCase, title: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Case Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={newCase.description}
                onChange={(e) =>
                  setNewCase({ ...newCase, description: e.target.value })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowCaseModal(false)}>
            Cancel
          </Button>
          <Button variant="success" onClick={handleFileCase}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
