import React, { useEffect } from "react";
import { Container, Navbar, Row, Col, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../auth/AuthContext";
import Footer from "../../layouts/Footer"; // ‚úÖ Shared footer

export default function Home() {
  const navigate = useNavigate();
  const { user } = useAuth();

  // ‚úÖ Redirect logged-in users by role
  useEffect(() => {
    if (user) {
      const roles = user.roles || [];
      if (roles.includes("CIVILIAN")) {
        navigate("/civilian/dashboard");
      } else if (roles.includes("PROSECUTOR")) {
        navigate("/prosecutor/dashboard");
      } else if (roles.includes("JUDGE")) {
        navigate("/judge/dashboard");
      } else if (roles.includes("REGISTRAR")) {
        navigate("/registrar/dashboard");
      }
    }
  }, [user, navigate]);

  // ‚úÖ Card click handler
  const handleCardClick = () => {
    navigate("/login");
  };

  return (
    <>
      {/* Navbar */}
      <Navbar bg="primary" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand>Case Management System</Navbar.Brand>
        </Container>
      </Navbar>

      {/* Main Section */}
      <Container
        fluid
        className="py-5"
        style={{ backgroundColor: "#f7f8fa", minHeight: "90vh" }}
      >
        <Container className="text-center">
          {/* Heading */}
          <h1 className="fw-bold display-4 mb-3">
            <span style={{ fontSize: "3rem" }}>üèõ</span> Welcome to the Case
            Management System
          </h1>
          <p className="text-muted lead mb-5">
            Secure access to case filing, evidence management, hearings, and
            billing.
          </p>

          {/* Role Cards */}
          <Row className="g-4 justify-content-center">
            <Col md={3} sm={6}>
              <Card
                className="shadow-sm border-0 h-100 text-center card-hover"
                style={{ backgroundColor: "#1e3a8a", color: "white" }}
                onClick={handleCardClick}
              >
                <Card.Body>
                  <Card.Title className="fw-bold fs-4">üë§ Civilian</Card.Title>
                  <Card.Text>
                    File cases, upload evidence, and track case progress.
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>

            <Col md={3} sm={6}>
              <Card
                className="shadow-sm border-0 h-100 text-center card-hover"
                style={{ backgroundColor: "#7c2d12", color: "white" }}
                onClick={handleCardClick}
              >
                <Card.Body>
                  <Card.Title className="fw-bold fs-4">
                    ‚öñÔ∏è Prosecutor
                  </Card.Title>
                  <Card.Text>
                    Manage assigned cases, review evidence, and submit charges.
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>

            <Col md={3} sm={6}>
              <Card
                className="shadow-sm border-0 h-100 text-center card-hover"
                style={{ backgroundColor: "#d97706", color: "white" }}
                onClick={handleCardClick}
              >
                <Card.Body>
                  <Card.Title className="fw-bold fs-4">üë®‚Äç‚öñÔ∏è Judge</Card.Title>
                  <Card.Text>
                    Review cases, schedule hearings, and deliver rulings.
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>

            <Col md={3} sm={6}>
              <Card
                className="shadow-sm border-0 h-100 text-center card-hover"
                style={{ backgroundColor: "#374151", color: "white" }}
                onClick={handleCardClick}
              >
                <Card.Body>
                  <Card.Title className="fw-bold fs-4">üìã Registrar</Card.Title>
                  <Card.Text>
                    Oversee the system, assign roles, verify evidence, and
                    manage billing.
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Container>
      </Container>

      {/* Footer */}
      <Footer />
    </>
  );
}
