import React, { useState } from "react";
import { useAuth } from "../../auth/AuthContext";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  InputGroup,
  Spinner,
} from "react-bootstrap";
import { EyeFill, EyeSlashFill } from "react-bootstrap-icons";

export default function LoginPage() {
  const { login } = useAuth();
  const navigate = useNavigate();

  // Local state
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // --- Handle login ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      // login() should return: { access_token, user: { email, role } }
      const response = await login(email, password);

      console.log("üîç Full backend response:", response);

      if (!response?.user) {
        throw new Error("No user object found in response");
      }
      if (!response.user.role) {
        throw new Error("No role found in response");
      }

      const role = response.user.role;
      console.log("‚úÖ Logged in user:", response.user);

      // ‚úÖ Redirect based on role
      switch (role.toUpperCase()) {
        case "CIVILIAN":
          navigate("/civilian");
          break;
        case "PROSECUTOR":
          navigate("/prosecutor");
          break;
        case "JUDGE":
          navigate("/judge");
          break;
        case "REGISTRAR":
          navigate("/registrar");
          break;
        default:
          console.warn("‚ö†Ô∏è Unknown role, redirecting to home:", role);
          navigate("/");
          break;
      }
    } catch (err) {
      console.error("‚ùå Login failed:", err);

      // Show backend error message if available
      const msg =
        err?.response?.data?.detail ||
        err?.message ||
        "Invalid email or password. Please try again.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center vh-100">
      <Row className="w-100">
        <Col md={{ span: 6, offset: 3 }}>
          <Card className="shadow-lg border-0">
            <Card.Body>
              <h3 className="text-center mb-4">üîê Login</h3>
              {error && <div className="alert alert-danger">{error}</div>}

              <Form onSubmit={handleSubmit}>
                {/* Email */}
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="Enter your email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </Form.Group>

                {/* Password */}
                <Form.Group className="mb-3">
                  <Form.Label>Password</Form.Label>
                  <InputGroup>
                    <Form.Control
                      type={showPassword ? "text" : "password"}
                      placeholder="Enter your password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                    <Button
                      variant="outline-secondary"
                      onClick={() => setShowPassword(!showPassword)}
                      type="button"
                    >
                      {showPassword ? <EyeSlashFill /> : <EyeFill />}
                    </Button>
                  </InputGroup>
                </Form.Group>

                {/* Submit */}
                <Button
                  variant="primary"
                  type="submit"
                  className="w-100"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <Spinner
                        as="span"
                        animation="border"
                        size="sm"
                        role="status"
                        aria-hidden="true"
                      />{" "}
                      Logging in...
                    </>
                  ) : (
                    "Login"
                  )}
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
